import{_ as a,c as r,o as t,a2 as o}from"./chunks/framework.v2dllGfT.js";const l="/images/qq.png",g=JSON.parse('{"title":"功能介绍","description":"","frontmatter":{},"headers":[],"relativePath":"docs/index.md","filePath":"docs/index.md"}'),s={name:"docs/index.md"};function i(h,e,c,b,n,d){return t(),r("div",null,e[0]||(e[0]=[o('<h1 id="功能介绍" tabindex="-1">功能介绍 <a class="header-anchor" href="#功能介绍" aria-label="Permalink to “功能介绍”">​</a></h1><h2 id="概要" tabindex="-1">概要 <a class="header-anchor" href="#概要" aria-label="Permalink to “概要”">​</a></h2><p>bbs-go 是一款基于 Go 语言研发的<code>开源</code>、<code>前后端分离</code>、<code>精美小巧</code>、<code>跨平台</code>的社区系统。初期该项目仅用过学习和交流，开源之后越来越多的小伙伴儿开始喜欢和关注他，这也是我长期升级和维护的动力。</p><p>bbs-go 为前后端分离设计，后端接口服务使用简洁的 Go 语言进行开发，前端页面使用 Vue.js 进行开发。系统由三个模块组成：</p><ul><li>server：go 语言开发后端接口服务，为系统提供数据支撑</li><li>site：基于 Nuxt.js 开发的社区前台 UI 服务</li><li>admin：基于 Vue.js 开发的社区运营</li></ul><h2 id="功能" tabindex="-1">功能 <a class="header-anchor" href="#功能" aria-label="Permalink to “功能”">​</a></h2><p><img src="https://bbs--go.oss-cn-hangzhou.aliyuncs.com/images/2025/03/11/8754103189670afe659f88d5596c8f2a.png" alt="bbs-go功能简介"></p><h2 id="官网" tabindex="-1">官网 <a class="header-anchor" href="#官网" aria-label="Permalink to “官网”">​</a></h2><ul><li>Github：<a href="https://github.com/mlogclub/bbs-go" target="_blank" rel="noreferrer">https://github.com/mlogclub/bbs-go</a></li><li>码云：<a href="https://gitee.com/mlogclub/bbs-go" target="_blank" rel="noreferrer">https://gitee.com/mlogclub/bbs-go</a></li><li>交流/演示站：<a href="https://bbs.bbs-go.com" target="_blank" rel="noreferrer">https://bbs.bbs-go.com</a></li></ul><h2 id="技术栈" tabindex="-1">技术栈 <a class="header-anchor" href="#技术栈" aria-label="Permalink to “技术栈”">​</a></h2><ul><li>Go (<a href="https://go.dev" target="_blank" rel="noreferrer">https://go.dev</a>) Build simple, secure, scalable systems with Go</li><li>MySQL (<a href="https://www.mysql.com" target="_blank" rel="noreferrer">https://www.mysql.com</a>) 全球广受欢迎的开源数据库</li><li>iris (<a href="https://github.com/kataras/iris" target="_blank" rel="noreferrer">https://github.com/kataras/iris</a>) Go 语言 MVC 框架</li><li>gorm (<a href="http://gorm.io" target="_blank" rel="noreferrer">http://gorm.io</a>) 最好用的 Go 语言数据库 ORM 框架</li><li>vue.js （<a href="https://vuejs.org" target="_blank" rel="noreferrer">https://vuejs.org</a>）渐进式 JavaScript 框架</li><li>Nuxt.js (<a href="https://nuxt.com" target="_blank" rel="noreferrer">https://nuxt.com</a>) 基于 Vue 的服务端渲染框架</li></ul><h2 id="交流群-联系我" tabindex="-1">交流群/联系我 <a class="header-anchor" href="#交流群-联系我" aria-label="Permalink to “交流群/联系我”">​</a></h2><p><img src="'+l+'" alt="bbs-go交流群"></p><h2 id="公众号" tabindex="-1">公众号 <a class="header-anchor" href="#公众号" aria-label="Permalink to “公众号”">​</a></h2><blockquote><p>关注公众号<code>码农俱乐部</code>获取更多干货资源。</p></blockquote><p><img src="https://open.weixin.qq.com/qr/code?username=gh_950827012b8d" alt="码农俱乐部"></p><h2 id="问题反馈" tabindex="-1">问题反馈 <a class="header-anchor" href="#问题反馈" aria-label="Permalink to “问题反馈”">​</a></h2><ul><li>问题反馈：<a href="https://bbs.bbs-go.com/topic/create?nodeId=6" target="_blank" rel="noreferrer">https://bbs.bbs-go.com/topic/create?nodeId=6</a></li><li>功能建议：<a href="https://bbs.bbs-go.com/topic/609" target="_blank" rel="noreferrer">https://bbs.bbs-go.com/topic/609</a></li></ul>',18)]))}const m=a(s,[["render",i]]);export{g as __pageData,m as default};
